<div class="container-fluid" id="CWrapper"> <div class="jumbotron-fluid map" id="mapLayout"> <iframe width="100%" frameborder="0" style="border:0" src="https://www.google.com/maps/embed/v1/place?q=place_id:ChIJ96jwfrMZ6zkR0k8ylcqaIdQ&key=AIzaSyD9jEQUCpuPO29oazXYRVEAdqXbulRNiNU" allowfullscreen></iframe> </div><div class="jumbotron"> <header> <h1>We'd love to hear from you</h1> </header> </div><?php foreach ($alldetails as $key=> $value): ?> <div> <p class="cM">Need to get in touch? Please fill out our form below or just dial the number below and we'll contact you as soon as possible.</p><p class="phone"> <strong><i class="fa fa-phone fa-lg"></i> <span class="contactContent"></span><?php echo ($value->Ophone)? "(+977)- 0".substr(($value->Ophone), 0, 1)."-".substr(($value->Ophone), -6) : 'Not available'; ?></strong> </p></div><div class="or"> <hr class="hrLine"></hr> <span><h3>Or</h3></span> </div><div class="row" style="padding: 2% 0 0 0;"> <div class="col-md-8"> <form data-parsley-required id="form"> <div class="form-group row"> <div class="col-md-12"> <input type="text" class="form-control" id="name" name="name" placeholder="Your Good Name" required> </div></div><div class="form-group row"> <div class="col-md-12"> <input type="email" class="form-control" id="email" name="email" placeholder="Your Email ID" required> </div></div><div class="form-group row"> <div class="col-md-12"> <input type="number" class="form-control" id="phone" name="phone" placeholder="Your Phone Number" required> </div></div><div class="form-group row"> <div class="col-md-12"> <input type="text" class="form-control" id="hear" name="hear" placeholder="How did you hear about us?" required> </div></div><div class="form-group row"> <div class="col-md-12"> <textarea class="form-control" id="message" name="message" placeholder="What should we do for you?" rows="5" cols="100%" required></textarea> </div></div><div class="form-group row"> <div class="col-md-12"> <button name="Submit" id="validate" value="Submit" class="btn btn-default">Submit</button> </div></div></form> </div><div class="col-md-4"> <div class="sidebarContact"> <h3>Our Location</h3> <ul> <li>Nepal</li><li><?php echo($value->location !="")? $value->location : 'Location Not Available'; ?></li><li>Lalitpur 44700</li><li>P.O.Box: <?php echo($value->pobox !="")? $value->pobox : 'Not Available'; ?></li><li>Fax: <?php echo($value->fax !="")? "(+977) 1 ".substr(($value->fax), 0, 3)." ".substr(($value->fax), 3, 4) : 'Not Available'; ?></li><li>Phone: <?php echo ($value->Ophone)? "(+977) 0".substr(($value->Ophone), 0, 1)." ".substr(($value->Ophone), -6) : 'Not available'; ?> </li><li>Email: <?php echo ($value->email !="")? $value->email : 'Not Available'; ?></li></ul> </div></div></div><?php endforeach; ?></div>
<script type="text/javascript">
$(document).ready(function(){$("#validate").click(function(){$("form").on("submit",function(e){e.preventDefault(),$.ajax({url:"controller/enquiry",type:"POST",data:$(this).serialize()}).done(function(){swal("Congratulation!","You have successfully sent the information!","success")}).fail(function(){swal("Opps!!","Please try again and check the info you have provided!","error")})})})});
</script>